mapper.select:
SELECT



FROM jiaoshi  a



        and a.id in

        #{item}



        and a.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')


        and a.jiaoshi_types = #{params.jiaoshiTypes}


        and a.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')


        and a.jiaoshi_shichang like CONCAT('%',#{params.jiaoshiShichang},'%')


        and a.sex_types = #{params.sexTypes}


        and a.zhuanye_types = #{params.zhuanyeTypes}


        and a.zhuanye_erji_types = #{params.zhuanyeErjiTypes}


        and a.jiaoshi_content like CONCAT('%',#{params.jiaoshiContent},'%')


          and a.jiaoshi_delete >= #{params.jiaoshiDeleteStart}   


          and a.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}   


        and a.jiaoshi_delete = #{params.jiaoshiDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



order by a.${params.sort} ${params.order}
